#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int N = 1e5 + 5;
const int M = 1e5 + 5;

int n, m;
char g[N][M];
int dx[] = {-1, 0, 1, 0};
int dy[] = {0, 1, 0, -1};
int vis[N][M];
int dis[N][M];
int pre[N][M];
int cnt;

void dfs(int x, int y) {
    vis[x][y] = 1;
    dis[x][y] = 1;
    pre[x][y] = cnt;
    cnt++;
    for (int i = 0; i < 4; i++) {
        int nx = x + dx[i];
        int ny = y + dy[i];
        if (nx >= 1 && nx <= n && ny >= 1 && ny <= m && g[nx][ny] == '.' && !vis[nx][ny]) {
            dfs(nx, ny);
            dis[x][y] = max(dis[x][y], dis[nx][ny] + 1);
        }
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);
    cin >> n >> m;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            cin >> g[i][j];
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            if (g[i][j] == '@') {
                dfs(i, j);
            }
        }
    }
    int mx = 0;
    int x, y;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            if (g[i][j] == '*') {
                for (int k = 0; k < 4; k++) {
                    int ni = i + dx[k];
                    int nj = j + dy[k];
                    if (ni >= 1 && ni <= n && nj >= 1 && nj <= m && g[ni][nj] == '.') {
                        int t = pre[ni][nj];
                        if (t != cnt && dis[t][pre[t][pre[t][pre[t][t]]]] + 1 == dis[i][j]) {
                            x = i;
                            y = j;
                            mx = max(mx, dis[i][j]);
                        }
                    }
                }
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            if (dis[i][j] == mx) {
                g[i][j] = '#';
            }
        }
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            cout << g[i][j];
        }
        cout << endl;
    }
    return 0;
}
