#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 16
#define INF 1000000000

typedef struct {
    double r, sx, sy, tx, ty;
} Coin;

int n;
Coin coins[N];

int overlap(int i, int j) {
    double dx = coins[i].tx - coins[j].tx;
    double dy = coins[i].ty - coins[j].ty;
    double d = sqrt(dx * dx + dy * dy);
    return d < (coins[i].r + coins[j].r) * 2;
}

int can_move(int i) {
    for (int j = 0; j < n; j++) {
        if (i == j) continue;
        if (overlap(i, j)) return 0;
    }
    return 1;
}

int dp[N][1 << N];

int dfs(int mask, int i) {
    if (i == n) return 0;
    if (dp[mask][i] != -1) return dp[mask][i];
    int res = 0;
    if (can_move(i)) {
        res = 1 + dfs(mask | (1 << i), i + 1);
    }
    res = max(res, dfs(mask, i + 1));
    return dp[mask][i] = res;
}

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%lf %lf %lf %lf %lf", &coins[i].r, &coins[i].sx, &coins[i].sy, &coins[i].tx, &coins[i].ty);
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            dp[1 << i][j] = -1;
        }
    }
    printf("%d\n", dfs(0, 0));
    return 0;
}
