#include <iostream>
#include <vector>
#include <algorithm>
#include <cmath>

using namespace std;

const int N = 16;
const int INF = 1e9;

struct Point {
    int x, y;
};

struct Coin {
    double r;
    Point s, t;
};

bool cmp(const Coin& a, const Coin& b) {
    return a.s.x < b.s.x || a.s.x == b.s.x && a.s.y < b.s.y;
}

bool check(const vector<Coin>& coins, int i, int j) {
    for (int k = 0; k < i; k++) {
        if (pow(coins[k].s.x - coins[i].s.x, 2) + pow(coins[k].s.y - coins[i].s.y, 2) <= pow(coins[k].r + coins[i].r, 2)) return false;
        if (pow(coins[k].s.x - coins[j].s.x, 2) + pow(coins[k].s.y - coins[j].s.y, 2) <= pow(coins[k].r + coins[j].r, 2)) return false;
        if (pow(coins[k].s.x - coins[i].s.x, 2) + pow(coins[k].s.y - coins[i].s.y, 2) <= pow(coins[k].r + coins[i].r, 2) &&
            pow(coins[k].s.x - coins[j].s.x, 2) + pow(coins[k].s.y - coins[j].s.y, 2) <= pow(coins[k].r + coins[j].r, 2) &&
            pow(coins[i].s.x - coins[j].s.x, 2) + pow(coins[i].s.y - coins[j].s.y, 2) <= pow(coins[i].r + coins[j].r, 2)) return false;
    }
    return true;
}

int main() {
    int n;
    cin >> n;
    vector<Coin> coins(n);
    for (int i = 0; i < n; i++) {
        cin >> coins[i].r >> coins[i].s.x >> coins[i].s.y >> coins[i].t.x >> coins[i].t.y;
    }
    sort(coins.begin(), coins.end(), cmp);
    int ans = 0;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            if (check(coins, i, j)) {
                ans = max(ans, 2);
                break;
            }
        }
    }
    cout << ans << endl;
    return 0;
}
