import sys
import math

def read_input():
    N = int(input())
    coins = []
    for _ in range(N):
        r, sx, sy, tx, ty = map(int, input().split())
        coins.append((r, sx, sy, tx, ty))
    return N, coins

def distance(x1, y1, x2, y2):
    return math.sqrt((x1 - x2) ** 2 + (y1 - y2) ** 2)

def check_validity(coins, mask):
    for i in range(N):
        if mask[i]:
            continue
        for j in range(N):
            if mask[j]:
                continue
            if distance(coins[i][1], coins[i][2], coins[j][1], coins[j][2]) - coins[i][0] - coins[j][0] < 0:
                return False
            if distance(coins[i][3], coins[i][4], coins[j][3], coins[j][4]) - coins[i][0] - coins[j][0] < 0:
                return False
            x1, y1 = coins[i][1] + (coins[i][3] - coins[i][1]) * (mask[i] + mask[j]) / 2, coins[i][2] + (coins[i][4] - coins[i][2]) * (mask[i] + mask[j]) / 2
            x2, y2 = coins[j][1] + (coins[j][3] - coins[j][1]) * (mask[i] + mask[j]) / 2, coins[j][2] + (coins[j][4] - coins[j][2]) * (mask[i] + mask[j]) / 2
            if distance(x1, y1, x2, y2) - coins[i][0] - coins[j][0] < 0:
                return False
    return True

def solve(coins, mask):
    if check_validity(coins, mask):
        return sum(mask)
    max_score = 0
    for i in range(N):
        if mask[i]:
            continue
        new_mask = mask.copy()
        new_mask[i] = 1
        score = solve(coins, new_mask)
        if score > max_score:
            max_score = score
    return max_score

N, coins = read_input()
max_score = solve(coins, [0] * N)
print(max_score)
