#include <stdio.h>
#include <string.h>
#include <stdlib.h>

#define MOD 1000000007
#define MAXN 200005

int n, m;
char s[MAXN], t[MAXN];
int dp[MAXN], f[MAXN], g[MAXN];
int p[MAXN], fail[MAXN];
int cnt[MAXN];

void add(int *p, int x) {
    if (p[x] == 0) p[x] = ++m;
}

void build() {
    int i, j;
    for (i = 0; i <= m; i++) fail[i] = 0;
    fail[0] = 1;
    for (i = 1; i <= m; i++) {
        for (j = fail[i - 1]; j > 0; j = fail[j - 1]) {
            if (s[i - 1] == s[j - 1]) {
                fail[i] = j;
                break;
            }
        }
        if (s[i - 1] == s[fail[i] - 1]) fail[i] = fail[fail[i]];
    }
}

void solve() {
    int i, j, k;
    scanf("%d", &n);
    for (i = 1; i <= n; i++) {
        scanf("%s", s);
        add(p, strlen(s));
        cnt[p[strlen(s)]]++;
    }
    scanf("%s", t);
    m = 0;
    build();
    for (i = 1; i <= strlen(t); i++) {
        if (cnt[i] == 0) continue;
        for (j = 1; j <= i; j++) {
            if (cnt[j] == 0) continue;
            if (fail[i - j] > 0 && fail[i - j] <= j) {
                f[i] = (f[i] + f[i - j] * cnt[j]) % MOD;
            }
        }
        if (cnt[i] > 0) {
            f[i] = (f[i] + f[i - 1]) % MOD;
        }
        dp[i] = (dp[i] + f[i]) % MOD;
    }
    printf("%d\n", dp[strlen(t)]);
}

int main() {
    solve();
    return 0;
}
