#include <iostream>
#include <vector>
#include <string>
#include <algorithm>
#include <unordered_map>

using namespace std;

const int MOD = 1e9 + 7;

int main() {
    int N;
    cin >> N;

    vector<string> S(N);
    for (int i = 0; i < N; i++) {
        cin >> S[i];
    }

    string T;
    cin >> T;

    unordered_map<string, int> dp;
    dp[""] = 1;

    for (int i = 0; i < T.size(); i++) {
        unordered_map<string, int> next;
        for (auto& it : dp) {
            string s = it.first;
            int val = it.second;

            for (int j = 0; j < N; j++) {
                if (S[j].size() > T.size() - i) continue;
                if (T.substr(i, S[j].size()) == S[j]) {
                    string t = s + S[j];
                    next[t] = (next[t] + val) % MOD;
                }
            }
        }
        dp = next;
    }

    int ans = 0;
    for (auto& it : dp) {
        ans = (ans + it.second) % MOD;
    }

    cout << ans << endl;

    return 0;
}
