import java.io.*;
import java.util.*;

public class Main {
    static int N, K;
    static int[] a;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        K = Integer.parseInt(st.nextToken());
        a = new int[N + 1];
        st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= N; i++) {
            a[i] = Integer.parseInt(st.nextToken());
        }
        int[] b = new int[N + 1];
        for (int i = 1; i <= N; i++) {
            b[i] = a[i];
        }
        for (int i = 1; i <= N; i++) {
            for (int j = i; j <= N; j++) {
                b[i] ^= a[j];
            }
        }
        int l = 1, r = N * (N + 1) / 2;
        while (l < r) {
            int mid = (l + r) / 2;
            int cnt = 0;
            for (int i = 1; i <= N; i++) {
                int j = i;
                while (j <= N && cnt + j - i <= mid) {
                    cnt += j - i;
                    j++;
                }
                if (cnt + j - i > mid) break;
            }
            if (cnt >= mid) r = mid;
            else l = mid + 1;
        }
        System.out.println(b[l]);
    }
}
