#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <math.h>

#define MOD 1000000007
#define MAXN 505

int n, m;
int in[MAXN], out[MAXN];
int d[MAXN];
int vis[MAXN];
int q[MAXN];
int cnt;

void dfs(int u) {
    vis[u] = 1;
    for (int i = in[u]; i < out[u]; i++) {
        int v = d[i];
        if (!vis[v]) {
            dfs(v);
        }
    }
}

int main() {
    scanf("%d%d", &n, &m);
    for (int i = 0; i < m; i++) {
        int u, v;
        scanf("%d%d", &u, &v);
        d[out[u]] = v;
        out[u]++;
        in[v]++;
    }
    memset(vis, 0, sizeof(vis));
    dfs(1);
    for (int i = 1; i <= n; i++) {
        if (!vis[i]) {
            printf("-1\n");
            return 0;
        }
    }
    memset(vis, 0, sizeof(vis));
    vis[1] = 1;
    cnt = 0;
    int head = 0, tail = 0;
    q[tail++] = 1;
    while (head < tail) {
        int u = q[head++];
        for (int i = in[u]; i < out[u]; i++) {
            int v = d[i];
            if (!vis[v]) {
                vis[v] = 1;
                q[tail++] = v;
            }
        }
        cnt++;
    }
    printf("%d\n", cnt % MOD);
    return 0;
}
