#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int N = 505;
const int M = 200005;
const int mod = 1e9 + 7;

int n, m;
vector<int> e[N];
int in[N], out[N], d[N];
bool vis[N];

void bfs(int s) {
    queue<int> q;
    q.push(s);
    vis[s] = true;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : e[u]) {
            if (!vis[v]) {
                vis[v] = true;
                q.push(v);
                d[v] = d[u] + 1;
                in[v]++;
                out[u]++;
            }
        }
    }
}

int main() {
    cin >> n >> m;
    for (int i = 1; i <= m; i++) {
        int u, v;
        cin >> u >> v;
        e[u].push_back(v);
    }
    bfs(1);
    for (int i = 1; i <= n; i++) {
        if (in[i] == 0) {
            cout << "-1" << endl;
            return 0;
        }
    }
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        ans = (ans + out[i]) % mod;
    }
    for (int i = 1; i <= n; i++) {
        if (d[i] > ans) {
            ans = d[i];
        }
    }
    cout << ans << endl;
    return 0;
}
