import java.io.*;
import java.util.*;

public class Main {
    static final int N = 505;
    static final int M = 200005;
    static final int mod = (int) 1e9 + 7;
    static int n, m;
    static int[] head = new int[N];
    static int[] to = new int[M];
    static int[] nxt = new int[M];
    static int[] in = new int[N];
    static int[] f = new int[N];
    static int[] d = new int[N];
    static int[] q = new int[N];
    static int cnt;

    static void add(int u, int v) {
        to[++cnt] = v;
        nxt[cnt] = head[u];
        head[u] = cnt;
    }

    static void bfs(int s) {
        Arrays.fill(d, 0);
        int l = 0, r = 0;
        q[r++] = s;
        d[s] = 1;
        while (l < r) {
            int u = q[l++];
            for (int i = head[u]; i != 0; i = nxt[i]) {
                int v = to[i];
                if (d[v] == 0) {
                    d[v] = d[u] + 1;
                    q[r++] = v;
                }
            }
        }
    }

    static boolean dfs(int u) {
        if (u == 1) return true;
        for (int i = head[u]; i != 0; i = nxt[i]) {
            int v = to[i];
            if (d[v] == d[u] + 1 && dfs(v)) {
                f[u] = v;
                return true;
            }
        }
        return false;
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        n = sc.nextInt();
        m = sc.nextInt();
        for (int i = 1; i <= m; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            add(u, v);
            in[v]++;
        }
        bfs(1);
        for (int i = 1; i <= n; i++) {
            if (in[i] == 0 && d[i] != 0) {
                System.out.println(-1);
                return;
            }
        }
        for (int i = 1; i <= n; i++) {
            if (d[i] != 0) {
                if (!dfs(i)) {
                    System.out.println(-1);
                    return;
                }
            }
        }
        int ans = 0;
        for (int i = 1; i <= n; i++) {
            if (f[i] != 0) {
                ans = (ans + d[f[i]]) % mod;
            }
        }
        System.out.println(ans);
    }
}

