from collections import deque
import sys
input = sys.stdin.readline

mod = 10**9 + 7

def bfs():
    q = deque()
    q.append(1)
    dist = [0] * (n + 1)
    dist[1] = 1
    while q:
        u = q.popleft()
        for v in adj[u]:
            if dist[v] == 0:
                dist[v] = dist[u] + 1
                q.append(v)
    return dist

def dfs(u, d):
    global ans
    if d > ans:
        return
    for v in adj[u]:
        dfs(v, d + 1)
    ans = max(ans, d)

n, m = map(int, input().split())
adj = [[] for _ in range(n + 1)]
for _ in range(m):
    u, v = map(int, input().split())
    adj[u].append(v)

dist = bfs()
ans = 0
for i in range(1, n + 1):
    if dist[i] == 0:
        dfs(i, 1)
        ans = max(ans, dist[i])
if ans == 0:
    print(-1)
else:
    print((ans - 1) % mod)
