#include <stdio.h>
#include <stdlib.h>

#define MAX_N 200000

long long d[MAX_N];
long long g[MAX_N];

int main() {
    int N;
    scanf("%d", &N);

    for (int i = 0; i < N - 1; i++) {
        scanf("%lld", &d[i]);
    }

    for (int i = 0; i < N; i++) {
        scanf("%lld", &g[i]);
    }

    long long min_cost = g[0];
    long long min_cost2 = g[0] + d[0];
    long long total_cost = g[0];

    for (int i = 1; i < N; i++) {
        long long cost = g[i] + min_cost;
        long long cost2 = g[i] + min_cost2 + d[i - 1];

        if (cost > cost2) {
            min_cost = min_cost2;
            min_cost2 = cost2;
        } else {
            min_cost2 = cost;
        }

        total_cost += min_cost;
    }

    printf("%lld\n", total_cost);

    return 0;
}
