import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        int n = scanner.nextInt();
        long[] d = new long[n - 1];
        long[] g = new long[n];

        for (int i = 0; i < n - 1; i++) {
            d[i] = scanner.nextLong();
        }

        for (int i = 0; i < n; i++) {
            g[i] = scanner.nextLong();
        }

        long[] dp = new long[n];
        dp[0] = g[0];
        for (int i = 1; i < n; i++) {
            dp[i] = Math.min(dp[i - 1] + d[i - 1], g[i]);
        }

        long result = Long.MAX_VALUE;
        for (int i = 0; i < n; i++) {
            result = Math.min(result, dp[i] + g[i] + sum(d, i));
        }

        System.out.println(result);
    }

    private static long sum(long[] d, int i) {
        long sum = 0;
        for (int j = i; j < d.length; j++) {
            sum += d[j];
        }
        return sum;
    }
}
