#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define N 100005
#define Q 100005

typedef struct {
    int l, r;
    long long p;
} node;

node a[N];
long long ans[N];
long long sum[N];

int cmp(const void *x, const void *y) {
    node *A = (node *)x;
    node *B = (node *)y;
    return (A->l - B->l);
}

int main() {
    int n, q;
    scanf("%d %d", &n, &q);
    for (int i = 1; i <= q; i++) {
        scanf("%d %d %lld", &a[i].l, &a[i].r, &a[i].p);
        a[i].r--;
    }
    qsort(a + 1, q, sizeof(node), cmp);
    ans[0] = 0;
    sum[0] = 0;
    for (int i = 1; i <= q; i++) {
        long long t = a[i].r - a[i].l + 1;
        long long l = a[i].l;
        long long r = a[i].r;
        long long p = a[i].p;
        long long now = ans[i - 1] + t;
        long long mx = 0;
        for (int j = i; j >= 1; j--) {
            if (a[j].r < l) break;
            if (a[j].l > r) break;
            mx = mx > sum[j - 1] ? mx : sum[j - 1];
            now = now < mx + a[j].p * t ? mx + a[j].p * t : now;
        }
        ans[i] = now;
        sum[i] = sum[i - 1] + p * t;
    }
    for (int i = 1; i <= q; i++) {
        printf("%lld\n", ans[i]);
    }
    return 0;
}
