#include <iostream>
#include <vector>
#include <algorithm>
#include <utility>

using namespace std;

typedef long long ll;
typedef pair<int, int> pii;

const int N = 1e5 + 5;

int n, q;
vector<pii> a[N];
ll dp[N];

void solve() {
    cin >> n >> q;
    for (int i = 1; i <= q; i++) {
        int a, b, p;
        cin >> a >> b >> p;
        a[i].push_back({a, p});
        a[i].push_back({b, -p});
    }
    for (int i = 1; i <= q; i++) {
        for (auto [x, y] : a[i]) {
            dp[i] = max(dp[i], dp[i - 1] + (x - 1));
            dp[i] = max(dp[i], dp[i - 1] + (n - x));
            dp[i] += y;
        }
        cout << dp[i] << '\n';
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    solve();
    return 0;
}
