import heapq
import sys

class SegmentTree:
    def __init__(self, n):
        self.n = n
        self.tree = [[0, 0] for _ in range(4 * n)]

    def update(self, node, start, end, pos, val):
        if start == end:
            self.tree[node][0] += val
            self.tree[node][1] = max(self.tree[node][1], val)
            return
        mid = (start + end) // 2
        if pos <= mid:
            self.update(2 * node, start, mid, pos, val)
        else:
            self.update(2 * node + 1, mid + 1, end, pos, val)
        self.tree[node][1] = max(self.tree[2 * node][1], self.tree[2 * node + 1][1])

    def query(self, node, start, end, l, r):
        if start > r or end < l:
            return 0
        if start >= l and end <= r:
            return self.tree[node][1]
        mid = (start + end) // 2
        return max(self.query(2 * node, start, mid, l, r), self.query(2 * node + 1, mid + 1, end, l, r))

def solve():
    n, q = map(int, input().split())
    seg = SegmentTree(n)
    pq = []
    time = 0
    for _ in range(q):
        a, b, p = map(int, input().split())
        a -= 1
        b -= 1
        time = max(time, b - a)
        seg.update(1, 0, n - 1, a, p)
        seg.update(1, 0, n - 1, b, -p)
        while pq and seg.tree[1][1] == 0:
            _, pos = heapq.heappop(pq)
            seg.update(1, 0, n - 1, pos, -1e9)
        if pq and seg.tree[1][1] > 0:
            heapq.heappush(pq, (seg.query(1, 0, n - 1, 0, n - 1), n - 1))
        print(time)

if __name__ == "__main__":
    solve()
