#include <stdio.h>

int main() {
    int N, Q;
    int c[1000], t[100000], x[100000], d[100000];
    int count = 0;

    scanf("%d", &N);
    for (int i = 0; i < N; i++) {
        scanf("%d", &c[i]);
    }
    scanf("%d", &Q);
    for (int i = 0; i < Q; i++) {
        scanf("%d %d %d", &t[i], &x[i], &d[i]);
        count++;
    }

    for (int i = 0; i < Q; i++) {
        if (t[i] == 1) {
            if (c[x[i] - 1] < d[i]) {
                printf("%d\n", count);
                return 0;
            }
            c[x[i] - 1] += d[i];
        } else {
            if (c[x[i] - 1] < d[i]) {
                printf("%d\n", count);
                return 0;
            }
            c[x[i] - 1] -= d[i];
        }
        count++;
    }

    printf("0\n");

    return 0;
}
