#include <iostream>
#include <vector>
#include <string>
#include <sstream>

int main() {
    int N;
    std::cin >> N;

    std::vector<int> c(N);
    for (int i = 0; i < N; i++) {
        std::cin >> c[i];
    }

    int Q;
    std::cin >> Q;

    std::vector<std::vector<int>> instructions(Q, std::vector<int>(3));
    for (int i = 0; i < Q; i++) {
        std::cin >> instructions[i][0] >> instructions[i][1] >> instructions[i][2];
    }

    for (int i = 0; i < Q; i++) {
        int t = instructions[i][0];
        int x = instructions[i][1] - 1;
        int d = instructions[i][2];

        if (t == 1) {
            if (c[x] < d) {
                std::cout << x + 1 << std::endl;
                return 0;
            }
            c[x] += d;
        } else {
            if (c[x] < d) {
                std::cout << x + 1 << std::endl;
                return 0;
            }
            c[x] -= d;
        }
    }

    std::cout << 0 << std::endl;

    return 0;
}
