import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] c = new int[n];
        for (int i = 0; i < n; i++) {
            c[i] = scanner.nextInt();
        }
        int q = scanner.nextInt();
        int[][] instructions = new int[q][3];
        for (int i = 0; i < q; i++) {
            instructions[i][0] = scanner.nextInt();
            instructions[i][1] = scanner.nextInt() - 1;
            instructions[i][2] = scanner.nextInt();
        }
        int[] count = new int[n];
        for (int i = 0; i < q; i++) {
            int type = instructions[i][0];
            int x = instructions[i][1];
            int d = instructions[i][2];
            if (type == 1) {
                if (count[x] + d > c[x]) {
                    System.out.println(x + 1);
                    return;
                }
                count[x] += d;
            } else {
                if (count[x] < d) {
                    System.out.println(x + 1);
                    return;
                }
                count[x] -= d;
            }
        }
        System.out.println(0);
    }
}
