#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 60

int a[MAXN];
int dp[MAXN][MAXN];
int pre[MAXN][MAXN];
int idx[MAXN][MAXN];

int max(int a, int b) {
    return a > b ? a : b;
}

void print(int k) {
    if (k == 0) return;
    print(pre[k][k]);
    printf("%d\n", idx[k][k]);
    k = pre[k][k];
}

int main() {
    int n, k;
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j <= i; j++) {
            dp[i][j] = -1;
        }
    }
    dp[0][0] = 1;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j <= i; j++) {
            if (dp[i][j] == -1) continue;
            for (int k = i + 1; k < n; k++) {
                if (a[k] * dp[i][j] > dp[k][j + 1]) {
                    dp[k][j + 1] = a[k] * dp[i][j];
                    pre[k][j + 1] = i;
                    idx[k][j + 1] = j + 1;
                }
            }
        }
    }
    k = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j <= i; j++) {
            if (dp[i][j] > dp[k][0]) {
                k = i;
            }
        }
    }
    printf("%d\n", k + 1);
    print(k);
    return 0;
}
