import java.util.Arrays;
import java.util.Comparator;

public class Main {
    public static void main(String[] args) {
        int N = readInt();
        int[] a = new int[N];
        for (int i = 0; i < N; i++) {
            a[i] = readInt();
        }
        int[] dp = new int[N + 1];
        int[] pre = new int[N + 1];
        Arrays.fill(dp, -1);
        dp[0] = 0;
        for (int i = 0; i < N; i++) {
            for (int j = N - 1; j >= 0; j--) {
                if (dp[j] >= 0 && dp[j] * a[i] > dp[Math.min(j + 1, N)]) {
                    dp[Math.min(j + 1, N)] = dp[j] * a[i];
                    pre[Math.min(j + 1, N)] = i;
                }
            }
        }
        int k = 0;
        while (k < N && dp[k] == -1) {
            k++;
        }
        System.out.println(k);
        while (k > 0) {
            System.out.println(pre[k] + 1);
            k--;
        }
    }

    static int readInt() {
        try {
            return Integer.parseInt(readLine());
        } catch (NumberFormatException e) {
            return 0;
        }
    }

    static String readLine() {
        try {
            return java.util.Scanner.class.newInstance().useDelimiter("\\A").next();
        } catch (Exception e) {
            return "";
        }
    }
}
