#include <iostream>
#include <queue>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

const int dx[] = {0, 0, -1, 1};
const int dy[] = {-1, 1, 0, 0};

int bfs(const vector<string>& graph, int s, int t) {
    int h = graph.size();
    int w = graph[0].size();
    vector<vector<bool>> visited(h, vector<bool>(w, false));
    queue<pair<int, int>> q;
    q.push({s / w, s % w});
    visited[s / w][s % w] = true;
    int dist = 0;
    while (!q.empty()) {
        int size = q.size();
        for (int i = 0; i < size; ++i) {
            int x = q.front().first;
            int y = q.front().second;
            q.pop();
            if (x == t / w && y == t % w) return dist;
            for (int j = 0; j < 4; ++j) {
                int nx = x + dx[j];
                int ny = y + dy[j];
                if (nx >= 0 && nx < h && ny >= 0 && ny < w && !visited[nx][ny] && graph[nx][ny] != 'o') {
                    q.push({nx, ny});
                    visited[nx][ny] = true;
                }
            }
        }
        ++dist;
    }
    return -1;
}

int main() {
    int h, w;
    char s, t;
    cin >> h >> w >> s >> t;
    vector<string> graph(h);
    for (int i = 0; i < h; ++i) {
        cin >> graph[i];
    }
    int start = -1;
    int end = -1;
    for (int i = 0; i < h; ++i) {
        for (int j = 0; j < w; ++j) {
            if (graph[i][j] == s) {
                start = i * w + j;
            }
            if (graph[i][j] == t) {
                end = i * w + j;
            }
        }
    }
    cout << bfs(graph, start, end) << endl;
    return 0;
}
