from collections import deque

def bfs(graph, start, end):
    queue = deque([(start, 0)])
    visited = set()
    while queue:
        node, dist = queue.popleft()
        if node == end:
            return dist
        if node in visited:
            continue
        visited.add(node)
        for neighbor in get_neighbors(graph, node):
            queue.append((neighbor, dist + 1))
    return -1

def get_neighbors(graph, node):
    neighbors = []
    for i in range(len(graph)):
        for j in range(len(graph[i])):
            if graph[i][j] == node:
                if i > 0 and graph[i-1][j] == 'o':
                    neighbors.append(graph[i-1][j])
                if i < len(graph) - 1 and graph[i+1][j] == 'o':
                    neighbors.append(graph[i+1][j])
                if j > 0 and graph[i][j-1] == 'o':
                    neighbors.append(graph[i][j-1])
                if j < len(graph[i]) - 1 and graph[i][j+1] == 'o':
                    neighbors.append(graph[i][j+1])
    return neighbors

def main():
    H, W, s, t = input().split()
    H, W = int(H), int(W)
    graph = []
    for _ in range(H):
        graph.append(list(input()))
    print(bfs(graph, s, t))

if __name__ == "__main__":
    main()
