#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007

typedef struct {
    int num;
    int cnt;
} Node;

int cmp(const void *a, const void *b) {
    Node *x = (Node *)a;
    Node *y = (Node *)b;
    if (x->num != y->num) return x->num - y->num;
    return x->cnt - y->cnt;
}

int main() {
    int n;
    scanf("%d", &n);
    Node a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i].num);
        a[i].cnt = 1;
    }
    qsort(a, n, sizeof(Node), cmp);
    long long ans = 1;
    for (int i = 1; i < n; i++) {
        if (a[i].num == a[i - 1].num) {
            a[i].cnt += a[i - 1].cnt;
            ans = (ans * a[i].cnt) % MOD;
        } else {
            ans = (ans * a[i].cnt) % MOD;
        }
    }
    if (a[0].cnt == 1) ans = 0;
    printf("%lld\n", ans);
    return 0;
}
