#include <stdio.h>
#include <stdlib.h>

#define N 100000
#define Q 100000

int n, q;
int p[N + 1], c[N + 1];
int cntG[N + 1];
int cntW[N + 1];
int ans[Q + 1];

int find(int x) {
    if (p[x] == x) return x;
    return p[x] = find(p[x]);
}

void union_(int x, int y) {
    x = find(x);
    y = find(y);
    if (x != y) p[y] = x;
}

int main() {
    int i, j, v;

    scanf("%d %d", &n, &q);
    for (i = 2; i <= n; i++) {
        scanf("%d", &p[i]);
        union_(i, p[i]);
    }
    for (i = 1; i <= n; i++) {
        char ch;
        scanf(" %c", &ch);
        if (ch == 'G') cntG[find(i)]++;
        else cntW[find(i)]++;
    }
    for (i = 1; i <= q; i++) {
        scanf("%d", &v);
        if (cntG[find(v)] > cntW[find(v)]) ans[i] = 1;
        else ans[i] = 0;
    }
    for (i = 1; i <= q; i++) {
        if (ans[i]) printf("broccoli\n");
        else printf("cauliflower\n");
    }
    return 0;
}
