#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int N = 1e5 + 5;

int n, q;
vector<int> G[N];
int sz[N], cntG[N];
string c[N];

void dfs(int u, int p) {
    sz[u] = 1;
    for (int v : G[u]) {
        if (v != p) {
            dfs(v, u);
            sz[u] += sz[v];
            cntG[u] += cntG[v];
            if (c[v] == 'G') cntG[u]++;
        }
    }
}

void solve(int u) {
    int cnt = cntG[u];
    if (c[u] == 'G') cnt--;
    if (cnt > sz[u] - cnt) cout << "broccoli" << endl;
    else cout << "cauliflower" << endl;
    for (int v : G[u]) {
        if (v != G[u][0]) {
            if (c[v] == 'G') cntG[v]--;
            else cntG[v]++;
            if (c[v] == 'G') cnt--;
            else cnt++;
            if (cnt > sz[v] - cnt) cout << "broccoli" << endl;
            else cout << "cauliflower" << endl;
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> q;
    for (int i = 2; i <= n; i++) {
        int p;
        cin >> p;
        G[p].push_back(i);
    }
    for (int i = 1; i <= n; i++) cin >> c[i];
    dfs(1, 0);
    while (q--) {
        int u;
        cin >> u;
        solve(u);
    }

    return 0;
}
