#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct City {
    int x, y;
};

bool compareCities(const City& a, const City& b) {
    return a.x < b.x;
}

int main() {
    int n;
    cin >> n;

    vector<City> cities(n);
    for (int i = 0; i < n; i++) {
        cin >> cities[i].x >> cities[i].y;
    }

    sort(cities.begin(), cities.end(), compareCities);

    vector<int> maxDist(n);
    maxDist[0] = abs(cities[0].x - cities[1].x) > abs(cities[0].y - cities[1].y) ? abs(cities[0].x - cities[1].x) : abs(cities[0].y - cities[1].y);
    for (int i = 1; i < n - 1; i++) {
        maxDist[i] = max(abs(cities[i].x - cities[i + 1].x), abs(cities[i].y - cities[i + 1].y));
    }
    maxDist[n - 1] = abs(cities[n - 1].x - cities[n - 2].x) > abs(cities[n - 1].y - cities[n - 2].y) ? abs(cities[n - 1].x - cities[n - 2].x) : abs(cities[n - 1].y - cities[n - 2].y);

    int totalCost = 0;
    for (int i = 0; i < n - 1; i++) {
        totalCost += maxDist[i];
    }

    cout << totalCost << endl;

    return 0;
}
