import java.io.*;
import java.util.*;

public class Main {
    static int n, q;
    static int[] p, c, v;
    static int[] cntG;
    static int[] cntW;
    static int[] size;
    static int[] parent;
    static int[] time;
    static int[] low;
    static int[] dfn;
    static int timer;
    static List<Integer>[] g;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        n = Integer.parseInt(st.nextToken());
        q = Integer.parseInt(st.nextToken());
        p = new int[n + 1];
        c = new int[n + 1];
        v = new int[q + 1];
        cntG = new int[n + 1];
        cntW = new int[n + 1];
        size = new int[n + 1];
        parent = new int[n + 1];
        time = new int[n + 1];
        low = new int[n + 1];
        dfn = new int[n + 1];
        g = new List[n + 1];
        for (int i = 0; i <= n; i++) {
            g[i] = new ArrayList<>();
        }
        st = new StringTokenizer(br.readLine());
        for (int i = 2; i <= n; i++) {
            p[i] = Integer.parseInt(st.nextToken());
        }
        st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= n; i++) {
            c[i] = st.nextToken().charAt(0) == 'G' ? 1 : 0;
        }
        for (int i = 1; i <= q; i++) {
            v[i] = Integer.parseInt(br.readLine());
        }
        dfs(1);
        for (int i = 1; i <= q; i++) {
            int u = v[i];
            int t = time[u];
            while (true) {
                int x = dfn[u];
                if (x <= t && c[x] == 1) {
                    cntG[size[u]]--;
                    cntW[size[u]]++;
                    c[x] = 0;
                } else if (x <= t && c[x] == 0) {
                    cntG[size[u]]++;
                    cntW[size[u]]--;
                    c[x] = 1;
                }
                if (low[u] == t) {
                    break;
                }
                u = parent[u];
            }
            if (cntG[size[1]] > cntW[size[1]]) {
                pw.println("broccoli");
            } else {
                pw.println("cauliflower");
            }
        }
        pw.close();
    }

    static void dfs(int u) {
        size[u] = 1;
        time[u] = low[u] = ++timer;
        dfn[u] = timer;
        g[p[u]].add(u);
        for (int v : g[u]) {
            parent[v] = u;
            dfs(v);
            size[u] += size[v];
            low[u] = Math.min(low[u], low[v]);
        }
    }
}
