from collections import defaultdict

class Tree:
    def __init__(self, n):
        self.n = n
        self.parent = [0] * (n + 1)
        self.label = [0] * (n + 1)
        self.size = [0] * (n + 1)
        self.children = [[] for _ in range(n + 1)]

    def build(self, edges):
        for i in range(2, self.n + 1):
            self.parent[i] = edges[i - 2]
            self.children[self.parent[i]].append(i)

    def dfs(self, v):
        self.size[v] = 1
        for u in self.children[v]:
            self.dfs(u)
            self.size[v] += self.size[u]

    def query(self, v):
        if self.label[v] == 'G':
            return self.size[v]
        else:
            return self.n - self.size[v]

    def update(self, v):
        self.label[v] = 'W' if self.label[v] == 'G' else 'G'

def main():
    n, q = map(int, input().split())
    tree = Tree(n)
    edges = list(map(int, input().split()))
    tree.build(edges)
    labels = input()
    for i in range(1, n + 1):
        tree.label[i] = labels[i - 1]
    tree.dfs(1)
    for _ in range(q):
        v = int(input())
        g = tree.query(v)
        w = tree.n - g
        if g > w:
            print("broccoli")
        else:
            print("cauliflower")
        tree.update(v)

main()
