#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define N 205
#define M 2005

typedef struct edge {
    int v, d, c;
} edge;

int n, m, s, t;
edge e[M];
int dis[N], pre[N];
int d[N][N], c[N][N];

void dijkstra(int s) {
    for (int i = 1; i <= n; i++) {
        dis[i] = INT_MAX;
        pre[i] = 0;
    }
    dis[s] = 0;
    for (int i = 1; i <= n; i++) {
        int u = 0;
        for (int j = 1; j <= n; j++) {
            if (dis[j] < dis[u] || dis[j] == dis[u] && pre[j] < pre[u]) {
                u = j;
            }
        }
        pre[u] = i;
        for (int j = 1; j <= n; j++) {
            if (dis[u] + d[u][j] < dis[j]) {
                dis[j] = dis[u] + d[u][j];
            }
        }
    }
}

int main() {
    scanf("%d %d %d %d", &n, &m, &s, &t);
    for (int i = 1; i <= m; i++) {
        scanf("%d %d %d %d", &e[i].v, &e[i].d, &e[i].c, &e[i].d);
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            d[i][j] = INT_MAX;
        }
    }
    for (int i = 1; i <= m; i++) {
        d[e[i].v][e[i].v] = 0;
        d[e[i].v][e[i].v + n] = e[i].c;
        d[e[i].v + n][e[i].v] = 0;
        d[e[i].v + n][e[i].v + n] = e[i].c;
        d[e[i].v][e[i].v + n] = 0;
        d[e[i].v + n][e[i].v] = 0;
        d[e[i].v][e[i].v] = e[i].d;
        d[e[i].v + n][e[i].v + n] = e[i].d;
    }
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            for (int k = 1; k <= n; k++) {
                d[j][k] = (d[j][k] < d[j][i] + d[i][k] ? d[j][k] : d[j][i] + d[i][k]);
            }
        }
    }
    dijkstra(s);
    int ans = 0;
    for (int i = 1; i <= m; i++) {
        ans = (ans > dis[t] * e[i].c ? ans : dis[t] * e[i].c);
    }
    printf("%d\n", ans);
    return 0;
}
