#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

const int INF = numeric_limits<int>::max();

struct Edge {
    int to, cost, dist;
};

struct Compare {
    bool operator()(const pair<int, int>& a, const pair<int, int>& b) {
        return a.first > b.first;
    }
};

int main() {
    int N, M, s, t;
    cin >> N >> M >> s >> t;

    vector<vector<Edge>> G(N + 1);
    for (int i = 0; i < M; i++) {
        int u, v, d, c;
        cin >> u >> v >> d >> c;
        G[u].push_back({v, c, d});
    }

    vector<int> dist(N + 1, INF);
    priority_queue<pair<int, int>, vector<pair<int, int>>, Compare> pq;
    pq.push({0, s});
    dist[s] = 0;

    while (!pq.empty()) {
        int d = pq.top().first;
        int u = pq.top().second;
        pq.pop();

        if (d != dist[u]) continue;

        for (const auto& e : G[u]) {
            int v = e.to;
            int c = e.cost;
            int nd = dist[u] + e.dist;

            if (nd < dist[v]) {
                dist[v] = nd;
                pq.push({dist[v], v});
            } else if (nd == dist[v]) {
                pq.push({dist[v], v});
            }
        }
    }

    int ans = 0;
    for (int i = 0; i < M; i++) {
        int u, v, d, c;
        cin >> u >> v >> d >> c;

        int nd = dist[t] + d;
        int cost = 0;

        while (nd > dist[t]) {
            cost += c;
            nd -= 1;
        }

        ans = min(ans, cost);
    }

    cout << ans << endl;

    return 0;
}
