import java.io.*;
import java.util.*;

public class Main {
    static final int N = 205;
    static final int M = 2005;
    static int n, m, s, t;
    static int[] d = new int[N];
    static int[] c = new int[M];
    static int[][] f = new int[N][N];
    static int[][] g = new int[N][N];
    static int[][] h = new int[N][N];
    static int[][] dis = new int[N][N];
    static int[][] pre = new int[N][N];
    static int[] vis = new int[N];
    static int[] dep = new int[N];
    static int[] in = new int[N];
    static int[] out = new int[N];
    static int[] head = new int[N];
    static int[] to = new int[M];
    static int[] nxt = new int[M];
    static int[] w = new int[M];
    static int[] cost = new int[M];
    static int cnt;

    static void add(int u, int v, int w, int c) {
        to[cnt] = v;
        w[cnt] = w;
        cost[cnt] = c;
        nxt[cnt] = head[u];
        head[u] = cnt++;
    }

    static void dijkstra(int s) {
        Arrays.fill(d, Integer.MAX_VALUE);
        d[s] = 0;
        PriorityQueue<int[]> q = new PriorityQueue<>((a, b) -> a[0] - b[0]);
        q.offer(new int[] {0, s});
        while (!q.isEmpty()) {
            int[] u = q.poll();
            if (d[u[1]] != u[0]) continue;
            for (int i = head[u[1]]; i != -1; i = nxt[i]) {
                int v = to[i];
                if (d[v] > d[u[1]] + w[i]) {
                    d[v] = d[u[1]] + w[i];
                    q.offer(new int[] {d[v], v});
                }
            }
        }
    }

    static void spfa(int s) {
        Arrays.fill(d, Integer.MAX_VALUE);
        Arrays.fill(dep, 0);
        Arrays.fill(in, 0);
        Arrays.fill(out, 0);
        Arrays.fill(vis, 0);
        d[s] = 0;
        vis[s] = 1;
        Queue<Integer> q = new LinkedList<>();
        q.offer(s);
        while (!q.isEmpty()) {
            int u = q.poll();
            vis[u] = 0;
            for (int i = head[u]; i != -1; i = nxt[i]) {
                int v = to[i];
                if (d[v] > d[u] + w[i] + cost[i]) {
                    d[v] = d[u] + w[i] + cost[i];
                    dep[v] = dep[u] + 1;
                    in[v] = i;
                    out[v] = i ^ 1;
                    q.offer(v);
                }
            }
        }
    }

    static void dfs(int u, int fa) {
        for (int i = head[u]; i != -1; i = nxt[i]) {
            int v = to[i];
            if (v == fa) continue;
            dfs(v, u);
            g[u][v] = Math.max(g[u][v], g[v][fa] + w[i]);
        }
    }

    static void dfs2(int u, int fa) {
        for (int i = head[u]; i != -1; i = nxt[i]) {
            int v = to[i];
            if (v == fa) continue;
            h[u][v] = Math.max(h[u][v], h[v][fa] + w[i]);
            dfs2(v, u);
        }
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        n = sc.nextInt();
        m = sc.nextInt();
        s = sc.nextInt();
        t = sc.nextInt();
        for (int i = 1; i <= m; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            int w = sc.nextInt();
            int c = sc.nextInt();
            add(u, v, w, c);
            add(v, u, 0, 0);
        }
        for (int i = 1; i <= n; i++) {
            head[i] = -1;
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                dis[i][j] = Integer.MAX_VALUE;
                pre[i][j] = 0;
            }
        }
        for (int i = 1; i <= m; i++) {
            dis[s][to[i - 1]] = w[i - 1];
            pre[s][to[i - 1]] = i - 1;
        }
        for (int k = 1; k <= n; k++) {
            for (int i = 1; i <= n; i++) {
                for (int j = 1; j <= n; j++) {
                    if (dis[i][k] + dis[k][j] < dis[i][j]) {
                        dis[i][j] = dis[i][k] + dis[k][j];
                        pre[i][j] = pre[i][k];
                    }
                }
            }
        }
        int ans = 0;
        for (int i = 1; i <= m; i++) {
            int u = pre[s][t];
            if (u == 0) continue;
            if (u == i) {
                ans += cost[i - 1];
                continue;
            }
            spfa(s);
            int v = t;
            while (v != s) {
                int p = in[v];
                g[to[p]][to[p ^ 1]] = Math.max(g[to[p]][to[p ^ 1]], g[v][to[p]] - w[p]);
                h[to[p]][to[p ^ 1]] = Math.max(h[to[p]][to[p ^ 1]], h[v][to[p]] - w[p]);
                v = to[p ^ 1];
            }
            dfs(t, s);
            dfs2(t, s);
            int x = 0;
            for (int j = 1; j <= n; j++) {
                if (g[t][j] + h[t][j] >= dis[s][t]) {
                    x = Math.max(x, g[t][j] + h[t][j] - dis[s][t]);
                }
            }
            ans += x * cost[i - 1];
        }
        System.out.println(ans);
    }
}
