#include <stdio.h>
#include <string.h>
#include <stdbool.h>

#define MAXN 100005

char s[MAXN];
int n, m;
int len[MAXN];
int dp[MAXN][2];

int main() {
    scanf("%s%d", s, &n);
    int slen = strlen(s);
    for (int i = 0; i < n; i++) {
        char p[MAXN];
        scanf("%s", p);
        int plen = strlen(p);
        for (int j = 0; j < slen; j++) {
            if (j + plen <= slen && memcmp(s + j, p, plen) == 0) {
                len[j] = plen;
                break;
            }
        }
    }
    for (int i = 0; i < slen; i++) {
        if (len[i] == 0) {
            len[i] = 1;
        }
    }
    for (int i = 0; i < slen; i++) {
        dp[i][0] = dp[i][1] = 0;
    }
    for (int i = 0; i < slen; i++) {
        if (len[i] == 1) {
            dp[i][0] = 1;
        }
        if (i > 0) {
            dp[i][0] += dp[i - 1][0];
            dp[i][1] += dp[i - 1][1];
        }
        if (i - len[i] >= 0) {
            dp[i][0] = min(dp[i][0], dp[i - len[i]][0] + len[i] - 1);
            dp[i][1] = min(dp[i][1], dp[i - len[i]][1] + len[i] - 1);
        }
    }
    printf("%d\n", min(dp[slen - 1][0], dp[slen - 1][1]));
    return 0;
}
