import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[][] cities = new int[n][2];

        for (int i = 0; i < n; i++) {
            cities[i][0] = scanner.nextInt();
            cities[i][1] = scanner.nextInt();
        }

        Arrays.sort(cities, (a, b) -> a[0] - b[0]);

        int[] prefixMax = new int[n];
        prefixMax[0] = cities[0][1];
        for (int i = 1; i < n; i++) {
            prefixMax[i] = Math.max(prefixMax[i - 1], cities[i][1]);
        }

        int minCost = 0;
        for (int i = 0; i < n; i++) {
            int maxDiff = Math.max(Math.abs(cities[i][0] - cities[0][0]), Math.abs(cities[i][1] - prefixMax[i]));
            minCost += maxDiff;
        }

        System.out.println(minCost);
    }
}
