from collections import defaultdict

class ColorfulDrink:
    def __init__(self):
        self.colors = defaultdict(list)
        self.densities = {}

    def add_liquid(self, color, density):
        self.colors[color].append(density)
        self.densities[density] = color

    def can_serve(self, layers):
        stack = []
        for layer in layers:
            if not stack:
                stack.append(layer)
                continue
            if self.colors[layer] and self.colors[stack[-1]]:
                if self.colors[layer][-1] > self.colors[stack[-1]][-1]:
                    stack.append(layer)
                else:
                    return False
            else:
                return False
        return True

def main():
    drink = ColorfulDrink()
    n = int(input())
    for _ in range(n):
        color, density = input().split()
        drink.add_liquid(color, int(density))
    m = int(input())
    layers = []
    for _ in range(m):
        layers.append(input())
    if drink.can_serve(layers):
        print("Yes")
    else:
        print("No")

if __name__ == "__main__":
    main()
