#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 10010

typedef struct {
    int size;
    int price;
} Gift;

Gift gifts[MAXN];
int dp[MAXN][MAXN];

int main() {
    int C, N, M;
    scanf("%d %d %d", &C, &N, &M);

    for (int i = 0; i < N; i++) {
        scanf("%d %d", &gifts[i].size, &gifts[i].price);
    }

    for (int i = 0; i <= C; i++) {
        for (int j = 0; j <= N; j++) {
            dp[i][j] = 0;
        }
    }

    for (int i = 0; i <= C; i++) {
        for (int j = 1; j <= N; j++) {
            for (int k = 0; k <= i / gifts[j - 1].size; k++) {
                dp[i][j] = (dp[i][j] > dp[i - k * gifts[j - 1].size][j - 1] + k * gifts[j - 1].price) ? dp[i][j] : dp[i - k * gifts[j - 1].size][j - 1] + k * gifts[j - 1].price;
            }
        }
    }

    for (int i = 1; i <= M; i++) {
        printf("%d\n", dp[C][i]);
    }

    return 0;
}
