#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

struct Gift {
    int size, price;
};

bool compareGifts(const Gift& a, const Gift& b) {
    return a.price > b.price;
}

int main() {
    int C, N, M;
    cin >> C >> N >> M;

    vector<Gift> gifts(N);
    for (int i = 0; i < N; i++) {
        cin >> gifts[i].size >> gifts[i].price;
    }

    sort(gifts.begin(), gifts.end(), compareGifts);

    vector<int> dp(C + 1, 0);
    for (const Gift& gift : gifts) {
        for (int i = C; i >= gift.size; i--) {
            dp[i] = max(dp[i], dp[i - gift.size] + gift.price);
        }
    }

    for (int k = 1; k <= M; k++) {
        int maxPrice = 0;
        for (int i = 0; i <= C; i++) {
            maxPrice = max(maxPrice, dp[i] * (k / (i + 1)) + dp[i + 1] * ((k + 1) / (i + 2)));
        }
        cout << maxPrice << endl;
    }

    return 0;
}
