import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int C = scanner.nextInt();
        int N = scanner.nextInt();
        int M = scanner.nextInt();

        int[][] gifts = new int[N][2];
        for (int i = 0; i < N; i++) {
            gifts[i][0] = scanner.nextInt();
            gifts[i][1] = scanner.nextInt();
        }

        Arrays.sort(gifts, (a, b) -> a[0] == b[0] ? b[1] - a[1] : a[0] - b[0]);

        int[] dp = new int[C + 1];
        for (int i = 0; i < N; i++) {
            for (int j = C; j >= gifts[i][0]; j--) {
                dp[j] = Math.max(dp[j], dp[j - gifts[i][0]] + gifts[i][1]);
            }
        }

        int[] maxPrice = new int[M + 1];
        for (int k = 1; k <= M; k++) {
            for (int i = 0; i <= C; i++) {
                int count = (i + k - 1) / k;
                maxPrice[k] = Math.max(maxPrice[k], dp[i] * count);
            }
        }

        for (int k = 1; k <= M; k++) {
            System.out.println(maxPrice[k]);
        }
    }
}
