#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100000
#define MAXQ 100000
#define MAXL 2500000

typedef struct {
    char *str;
    int len;
} Word;

typedef struct {
    char *prefix;
    char *suffix;
} Query;

int main() {
    int N, Q;
    scanf("%d %d", &N, &Q);

    Word *words = (Word *)malloc(N * sizeof(Word));
    for (int i = 0; i < N; i++) {
        words[i].str = (char *)malloc(MAXL * sizeof(char));
        scanf("%s", words[i].str);
        words[i].len = strlen(words[i].str);
    }

    Query *queries = (Query *)malloc(Q * sizeof(Query));
    for (int i = 0; i < Q; i++) {
        queries[i].prefix = (char *)malloc(MAXL * sizeof(char));
        queries[i].suffix = (char *)malloc(MAXL * sizeof(char));
        scanf("%s %s", queries[i].prefix, queries[i].suffix);
    }

    int *count = (int *)malloc(Q * sizeof(int));
    for (int i = 0; i < Q; i++) {
        count[i] = 0;
    }

    for (int i = 0; i < N; i++) {
        char *str = words[i].str;
        int len = words[i].len;
        int j = 0;
        while (j < len) {
            int k = 0;
            while (k < len - j && str[k] == str[j]) k++;
            if (k > 0) {
                int l = 0;
                while (l < Q && strcmp(queries[l].prefix, str) != 0) l++;
                if (l < Q) {
                    int m = 0;
                    while (m < Q && strcmp(queries[m].suffix, &str[len - j]) != 0) m++;
                    if (m < Q) {
                        count[l]++;
                        count[m]--;
                    }
                }
            }
            j++;
        }
    }

    for (int i = 0; i < Q; i++) {
        printf("%d\n", count[i]);
    }

    for (int i = 0; i < N; i++) {
        free(words[i].str);
    }
    free(words);

    for (int i = 0; i < Q; i++) {
        free(queries[i].prefix);
        free(queries[i].suffix);
    }
    free(queries);
    free(count);

    return 0;
}
