from collections import defaultdict

class TrieNode:
    def __init__(self):
        self.children = defaultdict(TrieNode)
        self.count = 0

class Trie:
    def __init__(self):
        self.root = TrieNode()

    def insert(self, word):
        node = self.root
        for char in word:
            node = node.children[char]
            node.count += 1

    def query(self, prefix, suffix):
        prefix_len = len(prefix)
        suffix_len = len(suffix)
        node = self.root
        for i in range(prefix_len):
            char = prefix[i]
            if char not in node.children:
                return 0
            node = node.children[char]
        for i in range(suffix_len - 1, -1, -1):
            char = suffix[i]
            if char not in node.children:
                return 0
            node = node.children[char]
        return node.count

def main():
    n, q = map(int, input().split())
    trie = Trie()
    for _ in range(n):
        word = input()
        trie.insert(word)
    for _ in range(q):
        prefix, suffix = input().split()
        print(trie.query(prefix, suffix))

if __name__ == "__main__":
    main()
