#include <stdio.h>
#include <stdlib.h>
#include <math.h>

typedef struct {
    double x, y;
} Point;

typedef struct {
    Point p[3];
} Triangle;

double crossProduct(Point p1, Point p2) {
    return (p1.x * p2.y - p1.y * p2.x);
}

double area(Triangle t) {
    return abs(crossProduct(t.p[1] - t.p[0], t.p[2] - t.p[0]) / 2.0);
}

double gcd(double a, double b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

double lcm(double a, double b) {
    return (a / gcd(a, b)) * b;
}

double commonArea(Triangle t1, Triangle t2) {
    double a1 = area(t1);
    double a2 = area(t2);
    double s1 = 0, s2 = 0;
    for (int i = 0; i < 3; i++) {
        s1 += crossProduct(t1.p[(i + 1) % 3] - t1.p[i], t2.p[(i + 1) % 3] - t2.p[i]);
        s2 += crossProduct(t2.p[(i + 1) % 3] - t2.p[i], t1.p[(i + 1) % 3] - t1.p[i]);
    }
    double s = s1 + s2;
    double c = lcm(a1, a2);
    return c - abs(s / 2.0);
}

int main() {
    int n;
    scanf("%d", &n);
    Triangle t[n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < 3; j++) {
            scanf("%lf %lf", &t[i].p[j].x, &t[i].p[j].y);
        }
    }
    double common = area(t[0]);
    for (int i = 1; i < n; i++) {
        common = commonArea(t[0], t[i]);
    }
    printf("%.10lf\n", common);
    return 0;
}
