import java.io.*;
import java.util.*;

public class Main {
    static class Point {
        double x, y;

        public Point(double x, double y) {
            this.x = x;
            this.y = y;
        }
    }

    static class Line {
        double a, b, c;

        public Line(Point p1, Point p2) {
            a = p2.y - p1.y;
            b = p1.x - p2.x;
            c = p1.x * p2.y - p1.y * p2.x;
        }
    }

    static double cross(Line l1, Line l2) {
        return l1.a * l2.b - l1.b * l2.a;
    }

    static double dot(Line l1, Line l2) {
        return l1.a * l2.a + l1.b * l2.b;
    }

    static double dist(Line l1, Line l2) {
        return Math.abs(cross(l1, l2)) / Math.sqrt(dot(l1, l1));
    }

    static double area(Line l1, Line l2, Line l3) {
        double s = (dist(l1, l2) + dist(l2, l3) + dist(l3, l1)) / 2;
        return Math.sqrt(s * (s - dist(l1, l2)) * (s - dist(l2, l3)) * (s - dist(l3, l1)));
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int n = Integer.parseInt(br.readLine());
        Point[] p = new Point[n];
        for (int i = 0; i < n; i++) {
            StringTokenizer st = new StringTokenizer(br.readLine());
            p[i] = new Point(Double.parseDouble(st.nextToken()), Double.parseDouble(st.nextToken()));
            p[i] = new Point(Double.parseDouble(st.nextToken()), Double.parseDouble(st.nextToken()));
            p[i] = new Point(Double.parseDouble(st.nextToken()), Double.parseDouble(st.nextToken()));
        }
        Line[] l = new Line[n];
        for (int i = 0; i < n; i++) {
            l[i] = new Line(p[i], p[(i + 1) % n]);
        }
        double ans = area(l[0], l[1], l[2]);
        for (int i = 3; i < n; i++) {
            ans = Math.min(ans, area(l[i - 2], l[i - 1], l[i]));
        }
        System.out.printf("%.6f\n", ans);
    }
}
