import math

def get_area(x1, y1, x2, y2, x3, y3):
    return abs((x1 * (y2 - y3) + x2 * (y3 - y1) + x3 * (y1 - y2)) / 2.0)

def get_intersection(x1, y1, x2, y2, x3, y3, x4, y4):
    d = (x1 - x3) * (y3 - y4) - (y1 - y3) * (x3 - x4)
    if d == 0:
        return None
    u = ((x1 - x3) * (y1 - y4) - (y1 - y3) * (x1 - x4)) / d
    v = ((x2 - x1) * (y1 - y4) - (y2 - y1) * (x1 - x4)) / d
    if u >= 0 and u <= 1 and v >= 0 and v <= 1:
        return (x1 + u * (x2 - x1), y1 + u * (y2 - y1))
    return None

def get_common_area(triangles):
    common_area = triangles[0]
    for i in range(1, len(triangles)):
        intersection = get_intersection(common_area[0], common_area[1], common_area[2], common_area[3], common_area[4], common_area[5], triangles[i][0], triangles[i][1])
        if intersection is not None:
            intersection_area = get_area(intersection[0], intersection[1], triangles[i][0], triangles[i][1], triangles[i][2], triangles[i][3])
            common_area = (intersection[0], intersection[1], intersection[0], intersection[1], intersection[0], intersection[1])
            common_area_area = get_area(common_area[0], common_area[1], common_area[2], common_area[3], common_area[4], common_area[5])
            common_area_area = min(common_area_area, intersection_area)
        else:
            common_area_area = get_area(common_area[0], common_area[1], common_area[2], common_area[3], common_area[4], common_area[5])
            common_area_area = min(common_area_area, get_area(triangles[i][0], triangles[i][1], triangles[i][2], triangles[i][3], triangles[i][4], triangles[i][5]))
    return common_area_area

def main():
    n = int(input())
    triangles = []
    for _ in range(n):
        x1, y1, x2, y2, x3, y3 = map(int, input().split())
        triangles.append((x1, y1, x2, y2, x3, y3))
    print(get_common_area(triangles))

if __name__ == "__main__":
    main()
