#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define N 200005
#define Q 200005

int n, q, s, t;
int a[N], b[N], c[N], d[N], e[N], f[N], g[N], h[N], i[N], j[N], k[N], l[N], m[N], x[N];
int ans[N];

void update(int i, int x) {
    for (; i <= n; i += i & -i) {
        if (x > 0) {
            c[i] += x;
        } else {
            d[i] += -x;
        }
    }
}

int query(int i) {
    int s = 0;
    for (; i > 0; i -= i & -i) {
        s += c[i];
    }
    return s;
}

int query2(int i) {
    int s = 0;
    for (; i > 0; i -= i & -i) {
        s += d[i];
    }
    return s;
}

int main() {
    scanf("%d%d%d%d", &n, &q, &s, &t);
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
        b[i] = a[i] - a[i - 1];
        if (b[i] > 0) {
            e[i] = e[i - 1] + b[i] * s;
        } else {
            f[i] = f[i - 1] + b[i] * t;
        }
    }
    for (int i = 1; i <= q; i++) {
        scanf("%d%d%d", &j[i], &k[i], &x[i]);
        l[i] = k[i] - j[i] + 1;
    }
    for (int i = 1; i <= n; i++) {
        g[i] = h[i] = i;
    }
    for (int i = 1; i <= q; i++) {
        int x = x[i];
        if (x > 0) {
            for (int j = j[i]; j <= k[i]; j++) {
                a[j] += x;
                b[j] = a[j] - a[j - 1];
                if (b[j] > 0) {
                    e[j] = e[j - 1] + b[j] * s;
                } else {
                    f[j] = f[j - 1] + b[j] * t;
                }
                update(j, 1);
            }
        } else {
            x = -x;
            int p = query2(k[i]);
            int q = query2(j[i] - 1);
            int r = query(k[i]);
            int s = query(j[i] - 1);
            int t = e[k[i]] - e[j[i] - 1] + f[k[i]] - f[j[i] - 1];
            int u = p - q + r - s;
            if (u > 0) {
                for (int j = g[k[i]]; j >= j[i]; j = g[j - 1]) {
                    update(j, -1);
                    if (b[j] > 0) {
                        e[j] = e[j - 1] + b[j] * s;
                    } else {
                        f[j] = f[j - 1] + b[j] * t;
                    }
                }
                for (int j = h[k[i]]; j <= k[i]; j = h[j + 1]) {
                    update(j, 1);
                    if (b[j] > 0) {
                        e[j] = e[j - 1] + b[j] * s;
                    } else {
                        f[j] = f[j - 1] + b[j] * t;
                    }
                }
                g[j[i]] = j[i];
                h[k[i]] = k[i];
            } else {
                for (int j = g[k[i]]; j >= j[i]; j = g[j - 1]) {
                    update(j, -1);
                    if (b[j] > 0) {
                        e[j] = e[j - 1] + b[j] * s;
                    } else {
                        f[j] = f[j - 1] + b[j] * t;
                    }
                }
                for (int j = h[k[i]]; j <= k[i]; j = h[j + 1]) {
                    update(j, 1);
                    if (b[j] > 0) {
                        e[j] = e[j - 1] + b[j] * s;
                    } else {
                        f[j] = f[j - 1] + b[j] * t;
                    }
                }
                g[j[i]] = j[i];
                h[k[i]] = k[i];
            }
            for (int j = j[i]; j <= k[i]; j++) {
                a[j] += x;
                b[j] = a[j] - a[j - 1];
                if (b[j] > 0) {
                    e[j] = e[j - 1] + b[j] * s;
                } else {
                    f[j] = f[j - 1] + b[j] * t;
                }
                update(j, 1);
            }
        }
        ans[i] = e[n] - e[j[i] - 1] + f[n] - f[j[i] - 1];
        printf("%d\n", ans[i]);
    }
    return 0;
}
