import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int xor = 0;
        for (int i = 0; i < n; i++) {
            int a = scanner.nextInt();
            xor ^= a;
        }
        if (xor == 0) {
            System.out.println("Bob");
        } else {
            System.out.println("Alice");
        }
    }
}
