#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 1005

int n, m;
char s[MAXN];
int dp[MAXN][MAXN];

int dfs(int i, int j) {
    if (i == m) {
        return j == 0;
    }
    if (dp[i][j] != -1) {
        return dp[i][j];
    }
    int res = 0;
    if (s[i] != '+') {
        res = dfs(i + 1, j * 10 + s[i] - '0');
    }
    res |= dfs(i + 1, j);
    if (j > 0 && s[i] == '+') {
        res |= dfs(i + 1, j - 1);
    }
    return dp[i][j] = res;
}

int main() {
    scanf("%d %s", &n, s);
    m = strlen(s);
    memset(dp, -1, sizeof(dp));
    int ans = 0;
    for (int i = 0; i < m; i++) {
        if (s[i] == '+') {
            ans++;
        } else {
            int j = 0;
            while (i + j < m && s[i + j] != '+') {
                j++;
            }
            if (dfs(i + 1, j) && (i + j == m || s[i + j] == '+')) {
                ans = m - i - j;
                break;
            }
        }
    }
    if (ans == 0) {
        printf("%d\n", -1);
    } else {
        printf("%d\n", n < ans ? -1 : ans);
    }
    return 0;
}
