#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int N;
    string S;
    cin >> N >> S;

    int res = 0;
    int sum = 0;
    int i = 0;
    while (i < S.size()) {
        if (S[i] == '+') {
            if (i + 1 < S.size() && S[i + 1] == '0') {
                res = -1;
                break;
            }
            if (sum + 1 > N) {
                res = -1;
                break;
            }
            sum++;
            i++;
        } else {
            int num = 0;
            while (i < S.size() && S[i] != '+') {
                num = num * 10 + (S[i] - '0');
                i++;
            }
            if (sum + num > N) {
                res = -1;
                break;
            }
            sum += num;
        }
    }

    if (res == -1) {
        cout << -1 << endl;
    } else {
        cout << S.size() - i << endl;
    }

    return 0;
}
