#include <stdio.h>
#include <math.h>

#define N 200

int main() {
    int n;
    double x[N], y[N], r[N], s[N], t[N];
    double maxr, maxs, maxt;

    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%lf %lf", &x[i], &y[i]);
    }

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (i != j) {
                r[i] = sqrt(pow(x[i] - x[j], 2) + pow(y[i] - y[j], 2));
            }
        }
    }

    for (int i = 0; i < n; i++) {
        maxr = 0;
        for (int j = 0; j < n; j++) {
            if (i != j) {
                maxr = fmax(maxr, r[j]);
            }
        }
        s[i] = 2 * M_PI * maxr;
    }

    for (int i = 0; i < n; i++) {
        maxs = 0;
        for (int j = 0; j < n; j++) {
            if (i != j) {
                maxs = fmax(maxs, s[j]);
            }
        }
        t[i] = 2 * M_PI * maxs;
    }

    maxt = 0;
    for (int i = 0; i < n; i++) {
        maxt = fmax(maxt, t[i]);
    }

    double sum = 0;
    for (int i = 0; i < n; i++) {
        sum += 2 * M_PI * r[i];
    }

    printf("%.10lf\n", fmin(sum, maxt));

    return 0;
}
