#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int N = 200005;

int n, q, s, t;
int a[N], l[N], r[N], x[N];
long long ans[N];

struct Node {
    int l, r;
    long long sum;
};

Node tree[N * 4];

void pushup(int u) {
    tree[u].sum = tree[u * 2].sum + tree[u * 2 + 1].sum;
}

void build(int u, int l, int r) {
    tree[u].l = l;
    tree[u].r = r;
    tree[u].sum = 0;
    if (l == r) return;
    int mid = (l + r) >> 1;
    build(u * 2, l, mid);
    build(u * 2 + 1, mid + 1, r);
}

void update(int u, int l, int r, long long k) {
    if (tree[u].l >= l && tree[u].r <= r) {
        tree[u].sum += k * (tree[u].r - tree[u].l + 1);
        return;
    }
    int mid = (tree[u].l + tree[u].r) >> 1;
    if (l <= mid) update(u * 2, l, r, k);
    if (r > mid) update(u * 2 + 1, l, r, k);
    pushup(u);
}

long long query(int u, int l, int r) {
    if (tree[u].l >= l && tree[u].r <= r) return tree[u].sum;
    int mid = (tree[u].l + tree[u].r) >> 1;
    long long res = 0;
    if (l <= mid) res += query(u * 2, l, r);
    if (r > mid) res += query(u * 2 + 1, l, r);
    return res;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    cin >> n >> q >> s >> t;
    for (int i = 1; i <= n; i++) cin >> a[i];
    for (int i = 1; i <= q; i++) cin >> l[i] >> r[i] >> x[i];
    build(1, 1, n);
    for (int i = 1; i <= n; i++) update(1, i, i, a[i] - a[i - 1]);
    for (int i = 1; i <= q; i++) {
        update(1, l[i], r[i], x[i]);
        ans[i] = query(1, 1, n) * t - (n - r[i] + 1) * s;
        if (a[r[i]] - a[l[i] - 1] > 0) ans[i] -= (a[r[i]] - a[l[i] - 1]) * s;
        else ans[i] += (a[r[i]] - a[l[i] - 1]) * t;
    }
    for (int i = 1; i <= q; i++) cout << ans[i] << "\n";
    return 0;
}
