#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAXN 100005

int is_prime(int n) {
    if (n <= 1) return 0;
    if (n <= 3) return 1;
    if (n % 2 == 0 || n % 3 == 0) return 0;
    for (int i = 5; i * i <= n; i += 6)
        if (n % i == 0 || n % (i + 2) == 0)
            return 0;
    return 1;
}

int main() {
    int N, M, S, T;
    scanf("%d %d %d %d", &N, &M, &S, &T);
    S--; T--;
    int dist[MAXN];
    for (int i = 0; i < N; i++)
        dist[i] = 1e9;
    dist[S] = 0;
    int queue[MAXN];
    int front = 0, rear = 0;
    queue[rear++] = S;
    while (front < rear) {
        int u = queue[front++];
        for (int i = 0; i < M; i++) {
            int v;
            scanf("%d", &v);
            v--;
            if (v == T) {
                printf("-1\n");
                return 0;
            }
            if (dist[v] == 1e9) {
                dist[v] = dist[u] + 1;
                queue[rear++] = v;
            }
        }
    }
    int min_dist = 1e9;
    for (int i = 0; i < N; i++)
        if (i != S && dist[i] != 1e9)
            min_dist = dist[i];
    if (min_dist == 1e9) {
        printf("-1\n");
        return 0;
    }
    for (int i = 2; i <= min_dist; i++)
        if (is_prime(i))
            if (i == min_dist) {
                printf("%d\n", i);
                return 0;
            }
            else
                break;
    printf("-1\n");
    return 0;
}
